import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faPlus, faCalendarCheck } from '@fortawesome/free-solid-svg-icons';
import './StudentDetails.css';
import { getStudents, deleteStudent } from '../api';

const StudentDetailsPage = () => {
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [isReportModalOpen, setReportModalOpen] = useState(false);
  const [marks, setMarks] = useState({});

  const terms = ['Term 1', 'Term 2', 'Final Term'];
  const subjects = ['Math', 'Science', 'English', 'History'];

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const students = await getStudents();
        setStudents(students);
      } catch (error) {
        console.error('Error fetching students:', error);
      }
    };

    fetchStudents();
  }, []);

  const openReportModal = (student) => {
    setSelectedStudent(student);
    setReportModalOpen(true);
  };

  const closeReportModal = () => {
    setReportModalOpen(false);
  };

  const calculateTermTotal = (studentMarks, term) => {
    return subjects.reduce((total, subject) => {
      const subjectMarks = studentMarks?.[term]?.[subject] || 0;
      return total + subjectMarks;
    }, 0);
  };

  const calculateOverallTotal = (studentMarks) => {
    return terms.reduce((total, term) => total + calculateTermTotal(studentMarks, term), 0);
  };

  return (
    <div className="students-container">
      <h1>Student Details</h1>

      {/* Report Modal */}
      {isReportModalOpen && (
        <div className="popup-form">
          <div className="form-container">
            <h2>Report for {selectedStudent?.name}</h2>
            <table>
              <thead>
                <tr>
                  <th>Term</th>
                  <th>Subject</th>
                  <th>Marks</th>
                </tr>
              </thead>
              <tbody>
                {terms.map((term) => (
                  <React.Fragment key={term}>
                    {subjects.map((subject) => (
                      <tr key={`${term}-${subject}`}>
                        <td>{term}</td>
                        <td>{subject}</td>
                        <td>
                          {marks[selectedStudent?.id]?.[term]?.[subject] || 0}
                        </td>
                      </tr>
                    ))}
                    <tr>
                      <td colSpan="2" style={{ fontWeight: 'bold' }}>
                        {term} Total
                      </td>
                      <td style={{ fontWeight: 'bold' }}>
                        {calculateTermTotal(marks[selectedStudent?.id], term)}
                      </td>
                    </tr>
                  </React.Fragment>
                ))}
                <tr>
                  <td colSpan="2" style={{ fontWeight: 'bold' }}>
                    Overall Total
                  </td>
                  <td style={{ fontWeight: 'bold' }}>
                    {calculateOverallTotal(marks[selectedStudent?.id])}
                  </td>
                </tr>
              </tbody>
            </table>
            <button onClick={closeReportModal}>Close</button>
          </div>
        </div>
      )}

      {/* Students Table */}
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Class</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.id}>
              <td>{student.name}</td>
              <td>{student.Class}</td>
              <td>
                <FontAwesomeIcon
                  icon={faPlus}
                  title="View Report"
                  onClick={() => openReportModal(student)}
                />
                <FontAwesomeIcon
                  icon={faTrash}
                  title="Delete"
                  onClick={() => deleteStudent(student.id)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentDetailsPage;
