// frontend/src/components/AdminPortal.jsx
import React, { useState } from 'react';
import axios from '../services/apiService';

const AdminPortal = () => {
    const [student, setStudent] = useState({ name: '', grade: '' });
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setStudent((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!student.name || !student.grade) {
            setError("Please fill out all fields");
            return;
        }
        try {
            await axios.post('/admin/students', student);
            setError('');
            alert('Student added successfully');
        } catch (error) {
            setError('Failed to add student');
        }
    };

    return (
        <div>
            <h2>Admin Portal - Manage Students</h2>
            <form onSubmit={handleSubmit}>
                <label>Student Name:</label>
                <input type="text" name="name" value={student.name} onChange={handleChange} />
                <label>Grade:</label>
                <select name="grade" value={student.grade} onChange={handleChange}>
                    <option value="">Select Grade</option>
                    <option value="1">1st Grade</option>
                    <option value="2">2nd Grade</option>
                    {/* Add more grades as necessary */}
                </select>
                <button type="submit">Save</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default AdminPortal;
