import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faEye,
  faEdit,
  faTrash,
  faDownload,
  faUserXmark,
  faPlus,
  faCalendarCheck,
} from '@fortawesome/free-solid-svg-icons';
import './StudentDetails.css';
import { Link } from 'react-router-dom';
import {
  addStudent,
  getStudentsMetrics,
  getStudents,
  deleteStudent,
  updateStudent,
  logout,
} from '../api';

const StudentDetailsPage = () => {
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [isMarksModalOpen, setMarksModalOpen] = useState(false);
  const [isAttendanceModalOpen, setAttendanceModalOpen] = useState(false);
  const [marks, setMarks] = useState({});
  const [attendance, setAttendance] = useState({});
  const terms = ['Term 1', 'Term 2', 'Final Term'];

  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const students = await getStudents();
        setStudents(students);
      } catch (error) {
        console.error('Error fetching students:', error);
      }
    };

    fetchStudents();
  }, []);

  const openMarksModal = (student) => {
    setSelectedStudent(student);
    setMarksModalOpen(true);
  };

  const closeMarksModal = () => {
    setMarksModalOpen(false);
  };

  const openAttendanceModal = (student) => {
    setSelectedStudent(student);
    setAttendanceModalOpen(true);
  };

  const closeAttendanceModal = () => {
    setAttendanceModalOpen(false);
  };

  const handleMarksSubmit = (e) => {
    e.preventDefault();
    // Logic to save marks
    setMarksModalOpen(false);
  };

  const handleAttendanceSubmit = (e) => {
    e.preventDefault();
    // Logic to save attendance
    setAttendanceModalOpen(false);
  };

  const calculatePromotion = (student) => {
    const studentMarks = marks[student.id] || {};
    const averageMarks =
      Object.values(studentMarks).reduce((sum, mark) => sum + mark, 0) /
      terms.length;
    const studentAttendance = attendance[student.id] || 0;
    const attendancePercentage = (studentAttendance / 200) * 100; // Assuming 200 school days

    return averageMarks >= 50 && attendancePercentage >= 75
      ? 'Promoted'
      : 'Not Promoted';
  };

  return (
    <div className="students-container">
      <h1>Student Details</h1>

      {/* Marks Modal */}
      {isMarksModalOpen && (
        <div className="popup-form">
          <div className="form-container">
            <h2>Add Marks for {selectedStudent?.name}</h2>
            <form onSubmit={handleMarksSubmit}>
              {terms.map((term) => (
                <div key={term} className="form-group">
                  <label>{term} Marks:</label>
                  <input
                    type="number"
                    min="0"
                    max="100"
                    value={marks[selectedStudent?.id]?.[term] || ''}
                    onChange={(e) =>
                      setMarks((prev) => ({
                        ...prev,
                        [selectedStudent.id]: {
                          ...prev[selectedStudent.id],
                          [term]: +e.target.value,
                        },
                      }))
                    }
                  />
                </div>
              ))}
              <button type="submit">Save Marks</button>
            </form>
            <button onClick={closeMarksModal}>Close</button>
          </div>
        </div>
      )}

      {/* Attendance Modal */}
      {isAttendanceModalOpen && (
        <div className="popup-form">
          <div className="form-container">
            <h2>Record Attendance for {selectedStudent?.name}</h2>
            <form onSubmit={handleAttendanceSubmit}>
              <div className="form-group">
                <label>Days Attended:</label>
                <input
                  type="number"
                  min="0"
                  max="200"
                  value={attendance[selectedStudent?.id] || ''}
                  onChange={(e) =>
                    setAttendance((prev) => ({
                      ...prev,
                      [selectedStudent.id]: +e.target.value,
                    }))
                  }
                />
              </div>
              <button type="submit">Save Attendance</button>
            </form>
            <button onClick={closeAttendanceModal}>Close</button>
          </div>
        </div>
      )}

      {/* Students Table */}
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Class</th>
            <th>Promotion Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.id}>
              <td>{student.name}</td>
              <td>{student.Class}</td>
              <td>{calculatePromotion(student)}</td>
              <td>
                <FontAwesomeIcon
                  icon={faPlus}
                  title="Add Marks"
                  onClick={() => openMarksModal(student)}
                />
                <FontAwesomeIcon
                  icon={faCalendarCheck}
                  title="Record Attendance"
                  onClick={() => openAttendanceModal(student)}
                />
                <FontAwesomeIcon
                  icon={faTrash}
                  title="Delete"
                  onClick={() => deleteStudent(student.id)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentDetailsPage;
