import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEdit, faTrash, faDownload } from '@fortawesome/free-solid-svg-icons';
import { faBell, faUsers, faGraduationCap, faCalendarAlt, faUserCheck, faBars, faDashboard, faSignOut } from '@fortawesome/free-solid-svg-icons';
import './Events.css';
import { Link } from 'react-router-dom';
import {} from '../api';


// Retrieve user data from localStorage
const username = localStorage.getItem('username');
const email = localStorage.getItem('email');

const ParentsPage = () => {
  // State for storing events data
  const [events, setEvents] = useState([]);
  const sortedEvents = [...events].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [filter, setFilter] = useState('');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const [activeForm, setActiveForm]= useState('');
  const [filterApplied, setFilterApplied] = useState(false);
// State for form inputs
 const [EventData, setEventData] = useState({ name: '', description: '' });

  // Form handling functions
  const handleEventChange = (e) => {
    const { name, value } = e.target;
    setEventData({ ...EventData, [name]: value });
  };

  // Side menu state
  const [menuOpen, setMenuOpen] = useState(false);

  // Profile dropdown state
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);


// Separate disclaimer states for each form
const [EventCreationDisclaimer, setParentCreationDisclaimer] = useState('');

  const openForm = (form) => {
    setActiveForm(form);
  };

  const closeForm = () => {
    setActiveForm('');
    setParentCreationDisclaimer('');
  };

  // Handle profile dropdown toggle
  const toggleProfileDropdown = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  // Toggle side menu
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };
    const [selectedParent, setSelectedParent] = useState(null);
    const [isViewModalOpen, setViewModalOpen] = useState(false);
    const [isEditModalOpen, setEditModalOpen] = useState(false);
    const [isDeleteModalOpen, setDeleteModalOpen] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;

    // Open View Modal
    const openViewModal = (event) => {
      setSelectedParent(event);
      setViewModalOpen(true);
    };

    // Open Edit Modal
    const openEditModal = (event) => {
      setSelectedParent(event);
