import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEdit, faTrash, faDownload } from '@fortawesome/free-solid-svg-icons';
import { faBell, faUsers, faGraduationCap, faCalendarAlt, faUserCheck, faUserGraduate, faBars, faUserGroup, faUserPlus, faUserPen, faUser, fa6, faSchool, faMagic, faDashboard, faSignOut } from '@fortawesome/free-solid-svg-icons';
import './Parents.css';
import { Link } from 'react-router-dom';


// Retrieve user data from localStorage
const username = localStorage.getItem('username');
const email = localStorage.getItem('email');

const API_URL = 'http://localhost:5000';
const ParentsPage = () => {
  // State for storing parents data
  const [parents, setParents] = useState([]);
  const [filter, setFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const [activeForm, setActiveForm]= useState('')
// State for form inputs
 const [parentData, setParentsData] = useState({ Name: '', phoneNumber: '' });

  // Side menu state
  const [menuOpen, setMenuOpen] = useState(false);

  // Profile dropdown state
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);


// Separate disclaimer states for each form
const [ParentCreationDisclaimer, setParentCreationDisclaimer] = useState('');
const [registerDisclaimer, setRegisterDisclaimer] = useState('');
const [resetDisclaimer, setResetDisclaimer] = useState('');


  const openForm = (form) => {
    setActiveForm(form);
  };

  const closeForm = () => {
    setActiveForm('');
    setParentCreationDisclaimer('');
    setRegisterDisclaimer('');
    setResetDisclaimer('');
  };

  // Handle profile dropdown toggle
  const toggleProfileDropdown = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  // Toggle side menu
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };
    const [selectedParent, setSelectedParent] = useState(null);
    const [isViewModalOpen, setViewModalOpen] = useState(false);
    const [isEditModalOpen, setEditModalOpen] = useState(false);
    const [isDeleteModalOpen, setDeleteModalOpen] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;

    // Open View Modal
    const openViewModal = (parent) => {
      setSelectedParent(parent);
      setViewModalOpen(true);
    };

    // Open Edit Modal
    const openEditModal = (parent) => {
      setSelectedParent(parent);
      setEditModalOpen(true);
    };

    // Open Delete Modal
    const openDeleteModal = (parent) => {
      setSelectedParent(parent);
      setDeleteModalOpen(true);
    };

    // Handle Delete Confirmation
    const confirmDelete = () => {
      deleteParent(selectedParent.id);
      setDeleteModalOpen(false);
    };

    // Close View Modal
    const closeViewModal = () => {
      setSelectedParent(null);
      setViewModalOpen(false);
      setEditModalOpen(false);
    };

    // Pagination logic
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentParents = parents.slice(indexOfFirstItem, indexOfLastItem);
    const totalPages = Math.ceil(parents.length / itemsPerPage);
 // Metrics state
 const [metrics, setMetrics] = useState({
  totalParents: 0,
  activeParents: 0,
  deletedParents: 0,
});


// Fetch parents from API on component mount
useEffect(() => {
  const fetchParents = async () => {
    try {
      const response = await fetch(`${API_URL}/api/parents/`);
      const data = await response.json();
      setParents(data); // Set parents data
    } catch (error) {
      console.error('Failed to fetch parents data', error);
    }
  };
  fetchParents();
}, []);

// Export functionality
const handleExport = () => {
  const csvContent = parents.map(parent => 
    `${parent.name},${parent.phoneNumber},${parent.status},${parent.dateAdded}`).join("\n");

  const blob = new Blob([csvContent], { type: 'text/csv' });
  const url = window.URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.setAttribute('href', url);
  a.setAttribute('download', 'parents_data.csv');
  a.click();
};

// Fetch user information and metrics from API on component mount
  useEffect(() => {

    const fetchMetrics = async () => {
      try {
        const data = await getDashboardMetrics();
        setMetrics(data); // Set metrics based on API response
      } catch (error) {
        console.error('Error fetching metrics:', error);
      }
    };

    fetchMetrics();
  }, []);

  // Parent Creation
  const handleParentSubmit = async (e) => {
    e.preventDefault();

    const { Name, PhoneNumber } = parentData;

    // Validate form fields
    if (!Name || !PhoneNumber) {
      setParentCreationDisclaimer('⚠️ Please fill in all fields.');
      return;
    }

    try {
      // Use the `addParent` API request function
      const result = await addParent({ name: Name, phoneNumber: PhoneNumber });

      // Success message
      setParentCreationDisclaimer(`✔️ Parent Creation successful: ${result.message}`);

      // Clear form fields
      setParentData({ Name: '', PhoneNumber: '' });

      // Hide the form or popup
      setShowLogin(false);

    } catch (error) {
      console.error('Error during Parent Creation:', error);
      setParentCreationDisclaimer(`⚠️ Parent Creation failed: ${error.message}`);
    }
  };  

  return (
    <div className="parents-wrapper">
      <div className={`parents-side-menu ${menuOpen ? 'open' : ''}`}>
  <button className="parents-menu-toggle" onClick={toggleMenu}>
    <FontAwesomeIcon icon={faBars} />
  </button>
  {/* Menu Icons */}
  <div className="parents-menu-icons">
  <ul>
        <li><Link to="/dashboard"><FontAwesomeIcon icon={faDashboard} className="Dashboard-icon" /> {menuOpen && <span>Dashboard</span>}</Link></li>
        <li><Link to="/notifications"><FontAwesomeIcon icon={faBell} className="Notifications-icon" /> {menuOpen && <span>Notifications</span>}</Link></li>
        <li><Link to="/students"><FontAwesomeIcon icon={faGraduationCap} className="Students-icon" /> {menuOpen && <span>Students</span>}</Link></li>
        <li><Link to="/events"><FontAwesomeIcon icon={faCalendarAlt} className="Events-icon" /> {menuOpen && <span>Events</span>}</Link></li>
        <li><Link to="/parents"><FontAwesomeIcon icon={faUsers} className="Parents-icon" /> {menuOpen && <span>Parents</span>}</Link></li>
        <li><Link to="/profile-dropdown"><FontAwesomeIcon icon={faUserCheck} className="Profile-icon" /> {menuOpen && <span>Profile</span>}</Link></li>
