import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './Dashboard.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell, faUsers, faGraduationCap, faCalendarAlt, faUserCheck, faUserGraduate, faBars, faUserGroup, faUserPlus, faUserPen, faUser, fa6, faSchool, faMagic, faDashboard, faSignOut, faCalendarCheck } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';
import { getEventsMetrics, getEvents, addEvent, deleteEvent, updateEvent} from '../api';
// Retrieve user data from localStorage
  const username = localStorage.getItem('username');
  const email = localStorage.getItem('email');

const Dashboard = () => {
// State for the metric data (fetched via API)
const [metrics, setMetrics] = useState({
  sentNotifications: 0,
  registeredParents: 0,
  registeredStudents: 0,
  createdEvents: 0,
  activeEvents: 0,
  activeStudents: 0,
});

// Fetch all metrics from APIs on component mount
useEffect(() => {
  const fetchAllMetrics = async () => {
    try {
      // Fetch data from each API endpoint
      const [eventsMetrics, parentsMetrics, studentsMetrics] = await Promise.all([
        getEventsMetrics(),
        getParentsMetrics(),
        getStudentsMetrics(),
      ]);

      // Combine data into one metrics object
      setMetrics(prevMetrics => ({
        ...prevMetrics,
        ...eventsMetrics,
        ...parentsMetrics,
        ...studentsMetrics,
      }));
    } catch (error) {
      console.error('Error fetching metrics:', error);
    }
  };

  fetchAllMetrics();
}, []);

  // State for filters
  const [filter, setFilter] = useState('');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  // Side menu state
