import React, { useState, useEffect } from 'react';
import './StudentDetails.css';
import { getSubjects, getStudents, getTerms} from '../api'; // Assuming these endpoints exist

const StudentDetailsPage = () => {
  const [students, setStudents] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [marks, setMarks] = useState({});
  const [currentTerm, setCurrentTerm] = useState(null);

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        // Fetch students
        const students = await getStudents();
        setStudents(students);

        // Fetch subjects
        const subjects = await getSubjects();
        setSubjects(subjects);

        // Fetch term details (including duration and current term)
        const termDetails = await getTerm();
        const now = new Date();

        // Determine the current term based on duration
        const activeTerm = termDetails.find(
          (term) => new Date(term.startDate) <= now && now <= new Date(term.endDate)
        );
        setCurrentTerm(activeTerm || termDetails[0]); // Default to first term if no active term
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchInitialData();
  }, []);

  const calculateTermTotal = (studentMarks, termId) => {
    return subjects.reduce((total, subject) => {
      const subjectMarks = studentMarks?.[termId]?.[subject.name] || 0;
      return total + subjectMarks;
    }, 0);
  };

  const calculateOverallTotal = (studentMarks) => {
    return currentTerm?.id
      ? calculateTermTotal(studentMarks, currentTerm.id)
      : 0;
  };

  return (
    <div className="students-container">
      <h1>{currentTerm ? `Current Term: ${currentTerm.name}` : 'No Active Term'}</h1>

      {currentTerm ? (
        <div className="term-report">
          <h2>Report for {currentTerm.name}</h2>
          <p>
            Duration: {currentTerm.startDate} to {currentTerm.endDate}
          </p>

          <table>
            <thead>
              <tr>
                <th>Name</th>
                {subjects.map((subject) => (
                  <th key={subject.id}>{subject.name}</th>
                ))}
                <th>Term Total</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student.id}>
                  <td>{student.name}</td>
                  {subjects.map((subject) => (
                    <td key={subject.id}>
                      {marks[student.id]?.[currentTerm.id]?.[subject.name] || 0}
                    </td>
                  ))}
                  <td>{calculateTermTotal(marks[student.id], currentTerm.id)}</td>
                </tr>
              ))}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan={subjects.length + 1} style={{ fontWeight: 'bold' }}>
                  Overall Total for All Students
                </td>
                <td style={{ fontWeight: 'bold' }}>
                  {students.reduce(
                    (sum, student) =>
                      sum + calculateTermTotal(marks[student.id], currentTerm.id),
                    0
                  )}
                </td>
              </tr>
            </tfoot>
          </table>
        </div>
      ) : (
        <p>No active term is currently ongoing.</p>
      )}
    </div>
  );
};

export default StudentDetailsPage;
