// frontend/src/components/TeacherPortal.jsx
import React, { useState } from 'react';
import axios from '../services/apiService';

const TeacherPortal = () => {
    const [attendance, setAttendance] = useState({ studentName: '', status: '' });
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setAttendance((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!attendance.studentName || !attendance.status) {
            setError("Please fill out all fields");
            return;
        }
        try {
            await axios.post('/teacher/attendance', attendance);
            setError('');
            alert('Attendance recorded successfully');
        } catch (error) {
            setError('Failed to record attendance');
        }
    };

    return (
        <div>
            <h2>Teacher Portal - Record Attendance</h2>
            <form onSubmit={handleSubmit}>
                <label>Student Name:</label>
                <input type="text" name="studentName" value={attendance.studentName} onChange={handleChange} />
                <label>Status:</label>
                <select name="status" value={attendance.status} onChange={handleChange}>
                    <option value="">Select Status</option>
                    <option value="present">Present</option>
                    <option value="absent">Absent</option>
                </select>
                <button type="submit">Submit</button>
            </form>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default TeacherPortal;
