import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './Dashboard.css'; // Assuming you use CSS for styling
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell, faUsers, faGraduationCap, faCalendarAlt, faUserCheck, faUserGraduate, faBars } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom'; // Assuming you're using react-router for page navigation

const Dashboard = () => {
  // State for the metric data (fetched via API)
  const [metrics, setMetrics] = useState({
    sentNotifications: 0,
    registeredParents: 0,
    registeredStudents: 0,
    createdEvents: 0,
    activeParents: 0,
    activeStudents: 0,
  });

  // State for filters
  const [filter, setFilter] = useState('all');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const [statusFilter, setStatusFilter] = useState('all'); // Additional filter for status
  const [typeFilter, setTypeFilter] = useState('all'); // Additional filter for type

  // Side menu state
  const [menuOpen, setMenuOpen] = useState(false);

  // Profile dropdown state
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  // Sample user info for profile dropdown
  const userInfo = {
    username: 'JohnDoe',
    email: 'johndoe@example.com',
  };

  // Handle profile dropdown toggle
  const handleProfileToggle = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  // Toggle side menu
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // Fetch metrics from API on component mount
  useEffect(() => {
    const fetchMetrics = async () => {
      // Sample API call to get metrics data
      try {
        const response = await fetch('/api/dashboard-metrics');
        const data = await response.json();
        setMetrics(data); // Set metrics based on API response
      } catch (error) {
        console.error('Failed to fetch dashboard metrics', error);
      }
    };
    fetchMetrics();
  }, []);

  return (
    <div className="dashboard-container">
      {/* Side Menu */}
      <div className={`side-menu ${menuOpen ? 'open' : ''}`}>
        <button className="menu-toggle" onClick={toggleMenu}>
          <FontAwesomeIcon icon={faBars} />
        </button>
        {menuOpen && (
          <nav className="menu-nav">
            <ul>
              <li><Link to="/dashboard">Dashboard</Link></li>
              <li><Link to="/notifications">Notifications</Link></li>
              <li><Link to="/students">Students</Link></li>
              <li><Link to="/events">Events</Link></li>
              <li><Link to="/profile">Profile</Link></li>
            </ul>
          </nav>
        )}
      </div>

      {/* Header Section */}
      <header className="dashboard-header">
        <h1 className="dashboard-title">Dashboard</h1>

        {/* Profile Icon */}
        <div className="profile-container" onClick={handleProfileToggle}>
          <img
            src="https://via.placeholder.com/50" // Replace with actual profile image URL
            alt="Profile"
            className="profile-icon"
          />
          {showProfileDropdown && (
            <div className="profile-dropdown">
              <p><strong>{userInfo.username}</strong></p>
              <p>{userInfo.email}</p>
              <button className="logout-button">Logout</button>
            </div>
          )}
        </div>
        {/* Side Menu */}
      <div className={`side-menu ${menuOpen ? 'open' : ''}`}>
        <button className="menu-toggle" onClick={toggleMenu}>
          <FontAwesomeIcon icon={faBars} />
        </button>
        {menuOpen && (
          <nav className="menu-nav">
            <ul>
              <li><Link to="/dashboard">Dashboard</Link></li>
              <li><Link to="/notifications">Notifications</Link></li>
              <li><Link to="/students">Students</Link></li>
              <li><Link to="/events">Events</Link></li>
              <li><Link to="/profile">Profile</Link></li>
            </ul>
          </nav>
        )}
      </div>
      </header>
      {/* Advanced Filters */}
      <div className="dashboard-filters">
        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="filter-select"
        >
          <option value="all">All</option>
          <option value="parents">Parents</option>
          <option value="students">Students</option>
          <option value="events">Events</option>
        </select>

        <select
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
          className="filter-select"
        >
          <option value="all">All Statuses</option>
          <option value="active">Active</option>
          <option value="inactive">Inactive</option>
        </select>

        <select
          value={typeFilter}
          onChange={(e) => setTypeFilter(e.target.value)}
          className="filter-select"
        >
          <option value="all">All Types</option>
          <option value="internal">Internal</option>
          <option value="external">External</option>
        </select>

        <div className="date-range">
          <label>Start Date: </label>
          <input
            type="date"
            value={dateRange.start}
            onChange={(e) =>
              setDateRange((prev) => ({ ...prev, start: e.target.value }))
            }
          />
          <label>End Date: </label>
          <input
            type="date"
            value={dateRange.end}
            onChange={(e) =>
              setDateRange((prev) => ({ ...prev, end: e.target.value }))
            }
          />
        </div>
      </div>

      {/* Metrics Cards */}
      <div className="metrics-container">
        <div className="metric-card">
          <FontAwesomeIcon icon={faBell} className="metric-icon" style={{ color: 'blue' }} />
          <h2>Sent Parent Notifications</h2>
          <p>{metrics.sentNotifications}</p>
        </div>
        <div className="metric-card">
          <FontAwesomeIcon icon={faUsers} className="metric-icon" style={{ color: 'green' }} />
          <h2>Registered Parents</h2>
          <p>{metrics.registeredParents}</p>
        </div>
        <div className="metric-card">
          <FontAwesomeIcon icon={faGraduationCap} className="metric-icon" style={{ color: 'purple' }} />
          <h2>Registered Students</h2>
          <p>{metrics.registeredStudents}</p>
        </div>
        <div className="metric-card">
          <FontAwesomeIcon icon={faCalendarAlt} className="metric-icon" style={{ color: 'orange' }} />
          <h2>Created Events</h2>
          <p>{metrics.createdEvents}</p>
        </div>
        <div className="metric-card">
          <FontAwesomeIcon icon={faUserCheck} className="metric-icon" style={{ color: 'red' }} />
          <h2>Active Parents</h2>
          <p>{metrics.activeParents}</p>
        </div>
        <div className="metric-card">
          <FontAwesomeIcon icon={faUserGraduate} className="metric-icon" style={{ color: 'brown' }} />
          <h2>Active Students</h2>
          <p>{metrics.activeStudents}</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
