import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEdit, faTrash, faBars, faDashboard, faBell, faUsers, faGraduationCap, faCalendarAlt, faUserCheck, faSignOut } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';
//import { addEvent, getEvents, deleteEvent, updateEvent } from '../api';
import './Events.css';

// Retrieve user data from localStorage
const username = localStorage.getItem('username');
const email = localStorage.getItem('email');

const EventsPage = () => {
  // State for storing events data
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [activeForm, setActiveForm] = useState('');
  const [menuOpen, setMenuOpen] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [eventData, setEventData] = useState({ name: '', description: '', date: '' });
  const [eventCreationDisclaimer, setEventCreationDisclaimer] = useState('');

  // Toggle side menu
  const toggleMenu = () => setMenuOpen(!menuOpen);

  // Fetch events data on component mount
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const events = await getEvents();
        setEvents(events);
        setFilteredEvents(events);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };
    fetchEvents();
  }, []);

  // Event form handling functions
  const handleEventChange = (e) => {
    const { name, value } = e.target;
    setEventData({ ...eventData, [name]: value });
  };

  const handleEventSubmit = async (e) => {
    e.preventDefault();
    const { name, description, date } = eventData;
    if (!name || !description || !date) {
      setEventCreationDisclaimer('⚠️ Please fill in all fields.');
      return;
    }
    try {
      const newEvent = await addEvent(eventData);
      setEvents([...events, newEvent]);
      setEventCreationDisclaimer('✔️ Event created successfully!');
      setEventData({ name: '', description: '', date: '' });
    } catch (error) {
      console.error('Error creating event:', error);
      setEventCreationDisclaimer(`⚠️ Event creation failed: ${error.message}`);
    }
  };

  const openForm = (form) => setActiveForm(form);
  const closeForm = () => {
    setActiveForm('');
    setEventCreationDisclaimer('');
  };

  return (
    <div className="events-wrapper">
      <div className={`events-side-menu ${menuOpen ? 'open' : ''}`}>
        <button className="events-menu-toggle" onClick={toggleMenu}>
          <FontAwesomeIcon icon={faBars} />
        </button>
        <div className="events-menu-icons">
          <ul>
            <li><Link to="/dashboard"><FontAwesomeIcon icon={faDashboard} /> {menuOpen && <span>Dashboard</span>}</Link></li>
            <li><Link to="/notifications"><FontAwesomeIcon icon={faBell} /> {menuOpen && <span>Notifications</span>}</Link></li>
            <li><Link to="/students"><FontAwesomeIcon icon={faGraduationCap} /> {menuOpen && <span>Students</span>}</Link></li>
            <li><Link to="/events"><FontAwesomeIcon icon={faCalendarAlt} /> {menuOpen && <span>Events</span>}</Link></li>
            <li><Link to="/parents"><FontAwesomeIcon icon={faUsers} /> {menuOpen && <span>Parents</span>}</Link></li>
            <li onClick={() => setShowProfileDropdown(!showProfileDropdown)}><FontAwesomeIcon icon={faUserCheck} />{menuOpen && <span>Profile</span>}</li>
            <li onClick={() => localStorage.clear()}><FontAwesomeIcon icon={faSignOut} />{menuOpen && <span>Logout</span>}</li>
          </ul>
        </div>
      </div>

      <div className={`events-container ${menuOpen ? 'blur' : ''}`}>
        <header className="events-header">
          <h1>Events</h1>
        </header>

        <div className="add-event-button-container">
          <button className="add-event-button" onClick={() => openForm('Event')}>+ Add Event</button>
        </div>

        {activeForm === 'Event' && (
          <div className="popup-form">
            <div className="form-container">
              <h2 className="form-title">Add Event</h2>
              <form onSubmit={handleEventSubmit} className="event-form">
                <div className="form-group">
                  <label htmlFor="eventName" className="form-label">Event Name:</label>
                  <input type="text" id="eventName" name="name" value={eventData.name} onChange={handleEventChange} className="form-input" required />
                </div>
                <div className="form-group">
                  <label htmlFor="eventDescription" className="form-label">Description:</label>
                  <textarea id="eventDescription" name="description" value={eventData.description} onChange={handleEventChange} className="form-input" required />
                </div>
                <div className="form-group">
                  <label htmlFor="eventDate" className="form-label">Date:</label>
                  <input type="date" id="eventDate" name="date" value={eventData.date} onChange={handleEventChange} className="form-input" required />
                </div>
                <div style={{ color: eventCreationDisclaimer.includes('✔️') ? 'green' : 'red', marginTop: '10px' }}>
                  {eventCreationDisclaimer}
                </div>
                <div className="event-form-actions">
                  <button type="submit" className="btn btn-primary">Create Event</button>
                  <button type="button" className="btn btn-close" onClick={closeForm}>Close</button>
                </div>
              </form>
            </div>
          </div>
        )}

        <div className="events-list">
          {events.map(event => (
            <div key={event.id} className="event-card">
              <h3>{event.name}</h3>
              <p>{event.description}</p>
              <p><strong>Date:</strong> {event.date}</p>
              <div className="event-actions">
                <button onClick={() => openEditModal(event)}><FontAwesomeIcon icon={faEdit} /></button>
                <button onClick={() => openDeleteModal(event)}><FontAwesomeIcon icon={faTrash} /></button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default EventsPage;
