import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEdit, faTrash, faDownload } from '@fortawesome/free-solid-svg-icons';
import './Parents.css'; // Assuming you have custom CSS for styling
import { Link } from 'react-router-dom';

const ParentsPage = () => {
  // State for storing parents data
  const [parents, setParents] = useState([]);
  const [filter, setFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });

  // Side menu state
  const [menuOpen, setMenuOpen] = useState(false);

  // Profile dropdown state
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  // Sample user info for profile dropdown
  const userInfo = {
    username: 'JohnDoe',
    email: 'johndoe@example.com',
  };

  // Handle profile dropdown toggle
  const toggleProfileDropdown = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  // Toggle side menu
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // Fetch metrics from API on component mount
  useEffect(() => {
    const fetchMetrics = async () => {
      // Sample API call to get metrics data
      try {
        const response = await fetch('/api/dashboard-metrics');
        const data = await response.json();
        setMetrics(data); // Set metrics based on API response
      } catch (error) {
        console.error('Failed to fetch dashboard metrics', error);
      }
    };
    fetchMetrics();
  }, []);
  // Metrics state
  const [metrics, setMetrics] = useState({
    totalParents: 0,
    activeParents: 0,
    deletedParents: 0,
  });

  // Fetch parents from API (mocked here)
  useEffect(() => {
    // Mocked data - replace this with your API fetch call
    const fetchedParents = [
      { id: 1, name: 'John Doe', status: 'active', dateAdded: '2024-01-01' },
      { id: 2, name: 'Jane Smith', status: 'active', dateAdded: '2024-02-01' },
      { id: 3, name: 'Michael Brown', status: 'deleted', dateAdded: '2023-12-15' },
    ];

    setParents(fetchedParents);

    // Calculate metrics
    const totalParents = fetchedParents.length;
    const activeParents = fetchedParents.filter(parent => parent.status === 'active').length;
    const deletedParents = fetchedParents.filter(parent => parent.status === 'deleted').length;

    setMetrics({ totalParents, activeParents, deletedParents });
  }, []);

  // Export functionality (mocked)
  const handleExport = () => {
    console.log("Exporting parents data..."); // Implement actual export logic
  };

  // Filter functionality (mocked)
  const handleFilter = () => {
    console.log("Filtering parents data..."); // Implement actual filter logic
  };

  return (
    <div className="parents-page-container">
      {/* Metric Cards */}
      <div className="metrics-container">
        <div className="metric-card">
          <h2>Total Parents</h2>
          <p>{metrics.totalParents}</p>
        </div>
        <div className="metric-card">
          <h2>Active Parents</h2>
          <p>{metrics.activeParents}</p>
        </div>
        <div className="metric-card">
          <h2>Deleted Parents</h2>
          <p>{metrics.deletedParents}</p>
        </div>
      </div>

      {/* Filter Section */}
      <div className="parents-filters">
        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="filter-select"
        >
          <option value="all">All</option>
          <option value="active">Active</option>
          <option value="deleted">Deleted</option>
        </select>

        <div className="date-range">
          <label>Start Date: </label>
          <input
            type="date"
            value={dateRange.start}
            onChange={(e) =>
              setDateRange((prev) => ({ ...prev, start: e.target.value }))
            }
          />
          <label>End Date: </label>
          <input
            type="date"
            value={dateRange.end}
            onChange={(e) =>
              setDateRange((prev) => ({ ...prev, end: e.target.value }))
            }
          />
        </div>

        <button className="filter-button" onClick={handleFilter}>Apply</button>
        <button className="export-button" onClick={handleExport}>
          <FontAwesomeIcon icon={faDownload} /> Export
        </button>
      </div>

      {/* Parents Table */}
      <table className="parents-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Status</th>
            <th>Date Added</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {parents.map((parent) => (
            <tr key={parent.id}>
              <td>{parent.name}</td>
              <td>{parent.status}</td>
              <td>{parent.dateAdded}</td>
              <td>
                <FontAwesomeIcon icon={faEye} className="action-icon" title="View" />
                <FontAwesomeIcon icon={faEdit} className="action-icon" title="Edit" />
                <FontAwesomeIcon icon={faTrash} className="action-icon" title="Delete" />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ParentsPage;
