import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faEdit, faTrash, faDownload } from '@fortawesome/free-solid-svg-icons';
import { faBell, faUsers, faGraduationCap, faCalendarAlt, faUserCheck, faUserGraduate, faBars, faUserGroup, faUserPlus, faUserPen, faUser, fa6, faSchool, faMagic, faDashboard, faSignOut } from '@fortawesome/free-solid-svg-icons';
import './Parents.css'; // Assuming you have custom CSS for styling
import { Link } from 'react-router-dom';

const ParentsPage = () => {
  // State for storing parents data
  const [parents, setParents] = useState([]);
  const [filter, setFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const [activeForm, setActiveForm]= useState('')
// State for form inputs
 const [parentData, setParentsData] = useState({ Name: '', phoneNumber: '' });

  // Side menu state
  const [menuOpen, setMenuOpen] = useState(false);

  // Profile dropdown state
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  // Sample user info for profile dropdown
  const userInfo = {
    username: 'JohnDoe',
    email: 'johndoe@example.com',
  };

// Separate disclaimer states for each form
const [ParentCreationDisclaimer, setParentCreationDisclaimer] = useState('');
const [registerDisclaimer, setRegisterDisclaimer] = useState('');
const [resetDisclaimer, setResetDisclaimer] = useState('');


  const openForm = (form) => {
    setActiveForm(form);
  };

  const closeForm = () => {
    setActiveForm('');
    setParentCreationDisclaimer('');
    setRegisterDisclaimer('');
    setResetDisclaimer('');
  };

  // Handle profile dropdown toggle
  const toggleProfileDropdown = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  // Toggle side menu
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };
  const ParentsTable = ({ parents, deleteParent, updateParent }) => {
    const [selectedParent, setSelectedParent] = useState(null);
    const [isViewModalOpen, setViewModalOpen] = useState(false);
    const [isEditModalOpen, setEditModalOpen] = useState(false);
    const [isDeleteModalOpen, setDeleteModalOpen] = useState(false);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;

    // Open View Modal
    const openViewModal = (parent) => {
      setSelectedParent(parent);
      setViewModalOpen(true);
    };

    // Open Edit Modal
    const openEditModal = (parent) => {
      setSelectedParent(parent);
      setEditModalOpen(true);
    };

    // Open Delete Modal
    const openDeleteModal = (parent) => {
      setSelectedParent(parent);
      setDeleteModalOpen(true);
    };

    // Handle Delete Confirmation
    const confirmDelete = () => {
      deleteParent(selectedParent.id);
      setDeleteModalOpen(false);
    };

    // Pagination logic
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentParents = parents.slice(indexOfFirstItem, indexOfLastItem);
    const totalPages = Math.ceil(parents.length / itemsPerPage);
  }
  // Fetch metrics from API on component mount
  useEffect(() => {
    const fetchMetrics = async () => {
      // Sample API call to get metrics data
      try {
        const response = await fetch('/api/dashboard-metrics');
        const data = await response.json();
        setMetrics(data); // Set metrics based on API response
      } catch (error) {
        console.error('Failed to fetch dashboard metrics', error);
      }
    };
    fetchMetrics();
  }, []);

  // Metrics state
  const [metrics, setMetrics] = useState({
    totalParents: 0,
    activeParents: 0,
    deletedParents: 0,
  });

  // Fetch parents from API (mocked here)
  useEffect(() => {
    // Mocked data - replace this with your API fetch call
    const fetchedParents = [
      { id: 1, name: 'John Doe', phoneNumber: '555-555-5555', status: 'active', dateAdded: '2024-01-01' },
      { id: 2, name: 'Jane Smith',phoneNumber: '555-555-5555', status: 'active', dateAdded: '2024-02-01' },
      { id: 3, name: 'Michael Brown',phoneNumber: '555-555-5555', status: 'deleted', dateAdded: '2023-12-15' },
    ];

    setParents(fetchedParents);

    // Calculate metrics
    const totalParents = fetchedParents.length;
    const activeParents = fetchedParents.filter(parent => parent.status === 'active').length;
    const deletedParents = fetchedParents.filter(parent => parent.status === 'deleted').length;

    setMetrics({ totalParents, activeParents, deletedParents });
  }, []);

  // Export functionality (mocked)
  const handleExport = () => {
    console.log("Exporting parents data..."); // Implement actual export logic
  };

  // Filter functionality (mocked)
  const handleFilter = () => {
    console.log("Filtering parents data..."); // Implement actual filter logic
  };
  const handleParentSubmit = async (e) => {
    e.preventDefault();

    const { Name, PhoneNumber,} = parentData;
    if (! Name || ! PhoneNumber) {
      setParentCreationDisclaimer('⚠️ Please fill in all fields.');
      return;
    }

    try {
      const response = await fetch(`${API_URL}/api/parents/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Name, PhoneNumber }),
      });

      const result = await response.json();
      if (response.ok) {
        setParentCreationDisclaimer(`✔️ Parent Creation successful: ${result.message}`);
        setParentsData({ Name: '', PhoneNumber: '' });
        setShowLogin(false);
      } else {
        setParentCreationDisclaimer(` ⚠️ Parent Creation failed: ${result.error}`);
      }
    } catch (error) {
      console.error('Error during Creation:', error);
      setParentCreationDisclaimer('⚠️ Login failed due to a network issue');
    }
  };

  return (
    <div className="parents-wrapper">
      <div className={`parents-side-menu ${menuOpen ? 'open' : ''}`}>
  <button className="parents-menu-toggle" onClick={toggleMenu}>
    <FontAwesomeIcon icon={faBars} />
  </button>
  {/* Menu Icons */}
  <div className="parents-menu-icons">
  <ul>
        <li><Link to="/dashboard"><FontAwesomeIcon icon={faDashboard} className="Dashboard-icon" /> {menuOpen && <span>Dashboard</span>}</Link></li>
        <li><Link to="/notifications"><FontAwesomeIcon icon={faBell} className="Notifications-icon" /> {menuOpen && <span>Notifications</span>}</Link></li>
        <li><Link to="/students"><FontAwesomeIcon icon={faGraduationCap} className="Students-icon" /> {menuOpen && <span>Students</span>}</Link></li>
        <li><Link to="/events"><FontAwesomeIcon icon={faCalendarAlt} className="Events-icon" /> {menuOpen && <span>Events</span>}</Link></li>
        <li><Link to="/parents"><FontAwesomeIcon icon={faUsers} className="Parents-icon" /> {menuOpen && <span>Parents</span>}</Link></li>
        <li><Link to="/profile-dropdown"><FontAwesomeIcon icon={faUserCheck} className="Profile-icon" /> {menuOpen && <span>Profile</span>}</Link></li>
        <li><Link to="/Logout"><FontAwesomeIcon icon={faSignOut} className="Register-icon" /> {menuOpen && <span>Logout</span>}</Link></li>

      </ul>
  </div>
</div>

{/* Main Content */}
      <div className={`parents-container ${menuOpen ? 'blur' : ''}`}>
        {/* Header Section */}
        <header className="parents-header">
          <h1 className="parents-title">Parents</h1>
        </header>
        {/* Profile Icon */}
        <div className="parents-profile-container" onClick={toggleProfileDropdown}>
            <FontAwesomeIcon icon={faUserCheck} className="parents-profile-icon" />
            {/* Profile Dropdown */}
            {showProfileDropdown && (
              <div className="parents-profile-dropdown">
                <p><strong><i>UserName: {userInfo.username}</i></strong></p>
                <p><strong><i>Email: {userInfo.email}</i></strong></p>
                <button className="parents-logout-button">Logout</button>
              </div>
            )}
          </div>
      {/* Metric Cards */}
      <div className="parents-metrics-container">
        <div className="parents-metric-card">
        <FontAwesomeIcon icon={faUsers} className="parent-metric-icon" style={{ color: 'green' }} />
            <h2>Total Parents</h2>
            <p>{metrics.totalParents}</p>
        </div>
        <div className="parents-metric-card">
        <FontAwesomeIcon icon={faUserCheck} className="parent-metric-icon" style={{ color: 'red' }} />
          <h2>Active Parents</h2>
          <p>{metrics.activeParents}</p>
        </div>
        <div className="parents-metric-card">
        <FontAwesomeIcon icon={faTrash} className="parent-metric-icon" style={{ color: 'black' }} />
          <h2>Deleted Parents</h2>
          <p>{metrics.deletedParents}</p>
        </div>
      </div>

      {/* Add Parent Button */}
      <div className="add-parent-button-container">
        <button className="add-parent-button" onClick={() => openForm('Parent')}> + Add Parent</button>
      </div>
      {/* Add Parent Form */}
{activeForm === 'Parent' && (
  <div className="popup-form">
    <div className="form-container">
      <h2 className="form-title">Add Parent</h2>
      <form onSubmit={handleParentSubmit} className="parent-form">
        <div className="form-group">
          <label htmlFor="ParentName" className="form-label">Name:</label>
          <input type="text" id="ParentName" required className="form-input" />
        </div>
        <div className="form-group">
          <label htmlFor="loginPassword" className="form-label">PhoneNumber:</label>
          <input type="text" id="ParentPhoneNumber" required className="form-input" />
        </div>
        <div style={{ color: ParentCreationDisclaimer.includes('✔️') ? 'green' : 'red', marginTop: '10px' }}>
          {ParentCreationDisclaimer}
        </div>
        <div className="parent-form-actions">
          <button type="submit" className="btn btn-primary">Create</button>
        </div>
      </form>
      <div className='form-close'>
      <button className="btn btn-close" onClick={closeForm}></button>
    </div>
  </div>
  </div>
)}

      {/* Filters Section */}
<div className="parents-filters">
  <h2 className="parents-filter-title">Filter By</h2>

  {/* Time Range Filter */}
  <div className="parents-filter-group">
    <label className="parents-filter-label">Time Range:</label>
    <select
      value={filter}
      onChange={(e) => setFilter(e.target.value)}
      className="parents-filter-select"
    >
      <option value="">Select Range</option>
      <option value="today">Today</option>
      <option value="lastweek">Last Week</option>
      <option value="last3months">Last 3 Months</option>
      <option value="year">Year</option>
      <option value="all">All Time</option>
    </select>
  </div>

  {/* From Date */}
  <div className="parents-filter-group">
    <label className="parents-filter-label">From:</label>
    <input
      type="date"
      value={dateRange.start}
      onChange={(e) =>
        setDateRange((prev) => ({ ...prev, start: e.target.value }))
      }
      className="filter-date"
    />
  </div>

  {/* To Date */}
  <div className="filter-group">
    <label className="filter-label">To:</label>
    <input
      type="date"
      value={dateRange.end}
      onChange={(e) =>
        setDateRange((prev) => ({ ...prev, end: e.target.value }))
      }
      className="filter-date"
    />
  </div>

  {/* Action Buttons */}
  <div className="filter-buttons">
    <button className="clear-button" onClick={() => { /* Clear Filters Logic */ }}>
      Clear
    </button>
    <button className="apply-button" onClick={() => { /* Apply Filters Logic */ }}>
      Filter
    </button>
        <button className="export-button" onClick={handleExport}>
          <FontAwesomeIcon icon={faDownload} /> Export
        </button>
      </div>
    </div>
      {/* Parents Table */}
      <table className="parents-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Phone Number</th>
            <th>Status</th>
            <th>Date Added</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {parents.map((parent) => (
            <tr key={parent.id}>
              <td>{parent.name}</td>
              <td>{parent.phoneNumber}</td>
              <td>{parent.status}</td>
              <td>{parent.dateAdded}</td>
              <td>
                <FontAwesomeIcon
                  icon={faEye}
                  className="action-icon"
                  title="View"
                  onClick={() => openViewModal(parent)}
                />
                <FontAwesomeIcon
                  icon={faEdit}
                  className="action-icon"
                  title="Edit"
                  onClick={() => openEditModal(parent)}
                />
                <FontAwesomeIcon
                  icon={faTrash}
                  className="action-icon"
                  title="Delete"
                  onClick={() => openDeleteModal(parent)}/>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
};

export default ParentsPage;
