import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCalendarAlt, faBars, faPlus, faDashboard, faSignOut } from '@fortawesome/free-solid-svg-icons';
import { addEvent, getEventMetrics, getEvents, deleteEvent, updateEvent } from '../api';
import './Events.css';
import { Link } from 'react-router-dom';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [metrics, setMetrics] = useState({ totalEvents: '', activeEvents: '' });
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [filter, setFilter] = useState('');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const [activeForm, setActiveForm] = useState('');
  const [eventData, setEventData] = useState({ name: '', description: '', date: '' });
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const eventList = await getEvents();
        setEvents(eventList);
        setFilteredEvents(eventList);
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };

    const fetchMetrics = async () => {
      try {
        const data = await getEventMetrics();
        setMetrics(data);
      } catch (error) {
        console.error('Error fetching metrics:', error);
      }
    };

    fetchEvents();
    fetchMetrics();
  }, []);

  const openForm = (form) => setActiveForm(form);
  const closeForm = () => {
    setActiveForm('');
    setEventData({ name: '', description: '', date: '' });
  };

  const handleEventChange = (e) => {
    const { name, value } = e.target;
    setEventData({ ...eventData, [name]: value });
  };

  const handleEventSubmit = async (e) => {
    e.preventDefault();
    const { name, description, date } = eventData;
    if (!name || !description || !date) {
      alert("Please fill in all fields.");
      return;
    }

    try {
      const result = await addEvent(eventData);
      setEvents([...events, result]);
      setEventData({ name: '', description: '', date: '' });
      closeForm();
    } catch (error) {
      console.error("Error adding event:", error);
    }
  };

  // Filter and Pagination logic
  const applyFilters = () => {
    let filtered = [...events];
    const now = new Date();
    if (filter === 'today') {
      filtered = filtered.filter(event => new Date(event.date).toDateString() === now.toDateString());
    } else if (filter === 'lastweek') {
      const lastWeek = new Date(now.setDate(now.getDate() - 7));
      filtered = filtered.filter(event => new Date(event.date) >= lastWeek);
    }
    setFilteredEvents(filtered);
  };

  return (
    <div className="events-wrapper">
      <div className={`events-side-menu ${menuOpen ? 'open' : ''}`}>
        <button className="events-menu-toggle" onClick={() => setMenuOpen(!menuOpen)}>
          <FontAwesomeIcon icon={faBars} />
        </button>
        <ul>
          <li><Link to="/dashboard"><FontAwesomeIcon icon={faDashboard} /> {menuOpen && <span>Dashboard</span>}</Link></li>
          <li><Link to="/events"><FontAwesomeIcon icon={faCalendarAlt} /> {menuOpen && <span>Events</span>}</Link></li>
          <li onClick={() => { /* logout function */ }}><FontAwesomeIcon icon={faSignOut} /> {menuOpen && <span>Logout</span>}</li>
        </ul>
      </div>

      <div className={`events-container ${menuOpen ? 'blur' : ''}`}>
        <header className="events-header">
          <h1 className="events-title">Events</h1>
          <button className="add-event-button" onClick={() => openForm('Event')}>+ Add Event</button>
        </header>

        <div className="events-metrics-container">
          <div className="events-metric-card">
            <h2>Total Events</h2>
            <p>{metrics.totalEvents}</p>
          </div>
          <div className="events-metric-card">
            <h2>Active Events</h2>
            <p>{metrics.activeEvents}</p>
          </div>
        </div>

        {activeForm === 'Event' && (
          <div className="popup-form">
            <div className="form-container">
              <h2>Add Event</h2>
              <form onSubmit={handleEventSubmit}>
                <input type="text" name="name" placeholder="Event Name" value={eventData.name} onChange={handleEventChange} required />
                <textarea name="description" placeholder="Event Description" value={eventData.description} onChange={handleEventChange} required />
                <input type="date" name="date" value={eventData.date} onChange={handleEventChange} required />
                <button type="submit">Create</button>
              </form>
              <button onClick={closeForm} className="close-button">Close</button>
            </div>
          </div>
        )}

        <div className="events-list">
          <h2>Events</h2>
          {filteredEvents.map((event, index) => (
            <div key={index} className="event-item">
              <p><strong>Name:</strong> {event.name}</p>
              <p><strong>Description:</strong> {event.description}</p>
              <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
              <button onClick={() => openForm('EditEvent')}>Edit</button>
              <button onClick={() => deleteEvent(event.id)}>Delete</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default EventsPage;
