import React, { useState, useEffect } from 'react';
import './StudentDetails.css';
import { getSubjects, getStudents, getTerms, addMarks } from '../api'; 

const StudentDetailsPage = () => {
  const [students, setStudents] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [marks, setMarks] = useState({});
  const [currentTerm, setCurrentTerm] = useState(null);
  const [isMarksModalOpen, setIsMarksModalOpen] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [newMarkData, setNewMarkData] = useState({
    subject: '',
    testType: 'test',
    markPercentage: ''
  });
  // Retrieve user data from localStorage
  const username = localStorage.getItem('username');
  const email = localStorage.getItem('email');

  const [terms, setTerms] = useState([]);
  // Side menu state
  const [menuOpen, setMenuOpen] = useState(false);

  // Profile dropdown state
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
    // Handle profile dropdown toggle
    const toggleProfileDropdown = () => {
      setShowProfileDropdown(!showProfileDropdown);
    };

    // Toggle side menu
    const toggleMenu = () => {
      setMenuOpen(!menuOpen);
    };

    const handleLogout = async () => {
      try {
        await logout();
        localStorage.removeItem('username');
        localStorage.removeItem('email');
        // Redirect to the index page
        window.location.href = '/';
      } catch (error) {
        console.error("Logout failed:", error);
      }
    };

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const students = await getStudents();
        setStudents(students);

        const subjects = await getSubjects();
        setSubjects(subjects);

        const termDetails = await getTerms();
        const now = new Date();

        const activeTerm = termDetails.find(
          (term) => new Date(term.startDate) <= now && now <= new Date(term.endDate)
        );
        setCurrentTerm(activeTerm || termDetails[0]); // Default to first term if no active term
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchInitialData();
  }, []);

  const handleMarksSubmit = async (e) => {
    e.preventDefault();
    try {
      await addMarks(selectedStudent.id, currentTerm.id, newMarkData);
      setMarks((prev) => ({
        ...prev,
        [selectedStudent.id]: {
          ...prev[selectedStudent.id],
          [currentTerm.id]: {
            ...prev[selectedStudent.id]?.[currentTerm.id],
            [newMarkData.subject]: {
              type: newMarkData.testType,
              percentage: newMarkData.markPercentage,
            },
          },
        },
      }));
      setIsMarksModalOpen(false); // Close the modal after submitting
    } catch (error) {
      console.error('Error adding marks:', error);
    }
  };

  const calculateSubjectTotal = (subjectMarks) => {
    const testMarks = subjectMarks?.test || 0;
    const examMarks = subjectMarks?.exam || 0;
    return testMarks + examMarks;
  };

  const calculateTermTotal = (studentMarks, termId) => {
    return subjects.reduce((total, subject) => {
      const subjectMarks = studentMarks?.[termId]?.[subject.name];
      return total + calculateSubjectTotal(subjectMarks);
    }, 0);
  };

  return (
    <div className="config-wrapper">
     {/* Side Menu */}
<div className={`side-menu ${menuOpen ? 'open' : ''}`}>
  <button className="menu-toggle" onClick={toggleMenu}>
    <FontAwesomeIcon icon={faBars} />
  </button>
  {/* Menu Icons */}
  <div className="menu-icons">
  <ul>
        <li><Link to="/dashboard"><FontAwesomeIcon icon={faDashboard} className="Dashboard-icon" /> {menuOpen && <span>Dashboard</span>}</Link></li>
        <li><Link to="/notifications"><FontAwesomeIcon icon={faBell} className="Notifications-icon" /> {menuOpen && <span>Notifications</span>}</Link></li>
        <li><Link to="/students"><FontAwesomeIcon icon={faGraduationCap} className="Students-icon" /> {menuOpen && <span>Students</span>}</Link></li>
        <li><Link to="/events"><FontAwesomeIcon icon={faCalendarAlt} className="Events-icon" /> {menuOpen && <span>Events</span>}</Link></li>
        <li><Link to="/parents"><FontAwesomeIcon icon={faUsers} className="Parents-icon" /> {menuOpen && <span>Parents</span>}</Link></li>
        <li><Link to="/config"><FontAwesomeIcon icon={faCog} className="Config-icon" /> {menuOpen && <span>Config</span>}</Link></li>
        <li onClick={toggleProfileDropdown}><FontAwesomeIcon icon={faUserCheck} className="Profile-icon" style={{ color: 'white' }} />{menuOpen && <span>Profile</span>}</li>
        <li onClick={handleLogout}><FontAwesomeIcon icon={faSignOut} className="Register-icon" style={{ color: 'white' }} />{menuOpen && <span>Logout</span>}</li>
      </ul>
  </div>
</div>
{/* Main Content */}
<div className={`config-container ${menuOpen ? 'blur' : ''}`}>
        {/* Header Section */}
        <header className="config-header">
          <h1 className="config-title">Configurations</h1>
        </header>
        {/* Profile Icon */}
        <div className="profile-container" onClick={toggleProfileDropdown}>
            <FontAwesomeIcon icon={faUserCheck} className="profile-icon" />
            {/* Profile Dropdown */}
            {showProfileDropdown && (
              <div className="profile-dropdown">
                <p><strong>UserName: {username}</strong></p>
                <p><strong>Email: {email}</strong></p>
                <button className="logout-button" onClick={handleLogout}>Logout</button>
              </div>
            )}
          </div>
    <div className="students-container">
      <h1>{currentTerm ? `Current Term: ${currentTerm.name}` : 'No Active Term'}</h1>

      {/* Add Marks Modal */}
      {isMarksModalOpen && (
        <div className="popup-form">
          <div className="form-container">
            <h2>Add Marks for {selectedStudent?.name}</h2>
            <form onSubmit={handleMarksSubmit}>
              <div className="form-group">
                <label>Subject</label>
                <select
                  value={newMarkData.subject}
                  onChange={(e) =>
                    setNewMarkData({ ...newMarkData, subject: e.target.value })
                  }
                >
                  {subjects.map((subject) => (
                    <option key={subject.id} value={subject.name}>
                      {subject.name}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Test Type</label>
                <select
                  value={newMarkData.testType}
                  onChange={(e) =>
                    setNewMarkData({ ...newMarkData, testType: e.target.value })
                  }
                >
                  <option value="test">Test</option>
                  <option value="exam">Exam</option>
                </select>
              </div>

              <div className="form-group">
                <label>Mark Percentage</label>
                <input
                  type="number"
                  value={newMarkData.markPercentage}
                  onChange={(e) =>
                    setNewMarkData({ ...newMarkData, markPercentage: e.target.value })
                  }
                  min="0"
                  max="100"
                />
              </div>

              <button type="submit">Submit Marks</button>
            </form>
            <button onClick={() => setIsMarksModalOpen(false)}>Close</button>
          </div>
        </div>
      )}

      {/* Term Report Table */}
      {currentTerm ? (
        <div className="term-report">
          <h2>Report for {currentTerm.name}</h2>
          <p>Duration: {currentTerm.startDate} to {currentTerm.endDate}</p>

          <table>
            <thead>
              <tr>
                <th>Student Name</th>
                {subjects.map((subject) => (
                  <th key={subject.id} colSpan="3">{subject.name}</th>
                ))}
                <th>Term Total</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student.id}>
                  <td>{student.name}</td>
                  {subjects.map((subject) => {
                    const subjectMarks = marks[student.id]?.[currentTerm.id]?.[subject.name] || {};
                    const subjectTotal = calculateSubjectTotal(subjectMarks);
                    return (
                      <>
                        <td key={`${subject.id}-test`}>Test: {subjectMarks?.test || 0}%</td>
                        <td key={`${subject.id}-exam`}>Exam: {subjectMarks?.exam || 0}%</td>
                        <td key={`${subject.id}-total`}>Total: {subjectTotal}%</td>
                      </>
                    );
                  })}
                  <td>{calculateTermTotal(marks[student.id], currentTerm.id)}%</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>No active term is currently ongoing.</p>
      )}
    </div>
    </div>
    </div>
  );
};

export default StudentDetailsPage;
