import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faBell, faUsers, faGraduationCap, faCalendarAlt, faUserCheck, faCog, faSignOut, faDashboard } from '@fortawesome/free-solid-svg-icons';
import { Link, useParams } from 'react-router-dom';
import './StudentDetails.css';
import { getSubjects, getStudent, getTerms, addMarks, getMarks } from '../api';

const StudentDetailsPage = () => {
  const { id } = useParams();
  const [subjects, setSubjects] = useState([]);
  const [marks, setMarks] = useState({});
  const [currentTerm, setCurrentTerm] = useState(null);
  const [isMarksModalOpen, setIsMarksModalOpen] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [newMarkData, setNewMarkData] = useState({ subject: '', testType: '', markPercentage: '' });
  const [terms, setTerms] = useState([]);
  const [menuOpen, setMenuOpen] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  // User data from localStorage
  const username = localStorage.getItem('username');
  const email = localStorage.getItem('email');

  // Fetch all necessary data on page load
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const student = await getStudent(id);
        setSelectedStudent(student);

        const subjects = await getSubjects();
        setSubjects(subjects);

        const termDetails = await getTerms();
        setTerms(termDetails);

        const now = new Date();
        const activeTerm = termDetails.find(
          (term) => new Date(term.startDate) <= now && now <= new Date(term.endDate)
        );
        setCurrentTerm(activeTerm || termDetails[0]);

        if (student && activeTerm) {
          await fetchMarks(student.id, activeTerm.id);
        }
      } catch (error) {
        console.error('Error fetching initial data:', error);
      }
    };

    fetchInitialData();
  }, [id]);

  // Fetch marks for a specific student and term
  const fetchMarks = async (studentId, termId) => {
    try {
      const fetchedMarks = await getMarks(studentId, termId);
      setMarks((prevMarks) => ({
        ...prevMarks,
        [studentId]: {
          ...prevMarks[studentId],
          [termId]: fetchedMarks,
        },
      }));
    } catch (error) {
      console.error('Error fetching marks:', error);
    }
  };

  // Handle adding new marks
  const handleMarksSubmit = async (e) => {
    e.preventDefault();
    if (!selectedStudent || !newMarkData.subject || !newMarkData.subject.id) {
      console.error('Error: Missing student or subject data');
      return;
    }

    try {
      await addMarks({
        studentId: selectedStudent.id,
        subjectId: newMarkData.subject.id,
        testType: newMarkData.testType,
        marks: newMarkData.markPercentage,
      });

      // Refresh marks after adding
      if (currentTerm) {
        await fetchMarks(selectedStudent.id, currentTerm.id);
      }

      setIsMarksModalOpen(false);
    } catch (error) {
      console.error('Error adding marks:', error);
    }
  };

  // Calculate totals
  const calculateSubjectTotal = (subjectMarks) => (subjectMarks?.test || 0) + (subjectMarks?.exam || 0);

  // UI actions
  const toggleMenu = () => setMenuOpen((prev) => !prev);
  const toggleProfileDropdown = () => setShowProfileDropdown((prev) => !prev);

  return (
    <div className="StudentDetails-wrapper">
      {/* Side Menu */}
      <div className={`side-menu ${menuOpen ? 'open' : ''}`}>
        <button className="menu-toggle" onClick={toggleMenu}>
          <FontAwesomeIcon icon={faBars} />
        </button>
        <ul>
          <li><Link to="/dashboard"><FontAwesomeIcon icon={faDashboard} /> Dashboard</Link></li>
          <li><Link to="/notifications"><FontAwesomeIcon icon={faBell} /> Notifications</Link></li>
          <li><Link to="/students"><FontAwesomeIcon icon={faGraduationCap} /> Students</Link></li>
          <li><Link to="/events"><FontAwesomeIcon icon={faCalendarAlt} /> Events</Link></li>
          <li><Link to="/parents"><FontAwesomeIcon icon={faUsers} /> Parents</Link></li>
          <li><Link to="/config"><FontAwesomeIcon icon={faCog} /> Configurations</Link></li>
          <li onClick={toggleProfileDropdown}><FontAwesomeIcon icon={faUserCheck} /> Profile</li>
          <li onClick={() => localStorage.clear()}><FontAwesomeIcon icon={faSignOut} /> Logout</li>
        </ul>
      </div>

      {/* Main Content */}
      <div className={`StudentDetails-container ${menuOpen ? 'blur' : ''}`}>
        <header className="StudentDetails-header">
          <h1>{currentTerm ? `Report: ${currentTerm.name}` : 'No Active Term'}</h1>
        </header>

        {isMarksModalOpen && (
          <div className="marks-popup-form">
            <form onSubmit={handleMarksSubmit}>
              <label>Subject</label>
              <select onChange={(e) => setNewMarkData({ ...newMarkData, subject: subjects.find((s) => s.name === e.target.value) })}>
                <option value="">Select Subject</option>
                {subjects.map((subject) => (
                  <option key={subject.id} value={subject.name}>{subject.name}</option>
                ))}
              </select>
              <label>Test Type</label>
              <select onChange={(e) => setNewMarkData({ ...newMarkData, testType: e.target.value })}>
                <option value="">Select Test Type</option>
                <option value="test">Test</option>
                <option value="exam">Exam</option>
              </select>
              <label>Mark Percentage</label>
              <input type="number" max="100" onChange={(e) => setNewMarkData({ ...newMarkData, markPercentage: e.target.value })} />
              <button type="submit">Save</button>
              <button onClick={() => setIsMarksModalOpen(false)}>Close</button>
            </form>
          </div>
        )}

        <table>
          <thead>
            <tr>
              <th>Subject</th>
              <th>Test</th>
              <th>Exam</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {subjects.map((subject) => {
              const subjectMarks = marks[selectedStudent?.id]?.[currentTerm?.id]?.[subject.name] || {};
              return (
                <tr key={subject.id}>
                  <td>{subject.name}</td>
                  <td>{subjectMarks?.test || '-'}</td>
                  <td>{subjectMarks?.exam || '-'}</td>
                  <td>{calculateSubjectTotal(subjectMarks)}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default StudentDetailsPage;
