import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faBell, faUsers, faGraduationCap, faCalendarAlt,
  faUserCheck, faBars, faCog, faDashboard, faSignOut
} from '@fortawesome/free-solid-svg-icons';
import { Link, useParams } from 'react-router-dom';
import './StudentDetails.css';
import { getSubjects, getStudent, getTerms, addMarks, getMarks } from '../api';

const StudentDetailsPage = () => {
  const { id } = useParams();
  const [subjects, setSubjects] = useState([]);
  const [marks, setMarks] = useState({});
  const [currentTerm, setCurrentTerm] = useState(null);
  const [isMarksModalOpen, setIsMarksModalOpen] = useState(false);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [newMarkData, setNewMarkData] = useState({
    subject: '',
    testType: '',
    markPercentage: '',
  });
  const [terms, setTerms] = useState([]);
  const [menuOpen, setMenuOpen] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);

  const username = localStorage.getItem('username');
  const email = localStorage.getItem('email');

  // Toggle functions
  const toggleMenu = () => setMenuOpen(!menuOpen);
  const toggleProfileDropdown = () => setShowProfileDropdown(!showProfileDropdown);

  // Logout
  const handleLogout = () => {
    localStorage.removeItem('username');
    localStorage.removeItem('email');
    window.location.href = '/';
  };

  // Fetch marks for the selected student and term
  const fetchMarksForStudent = async () => {
    if (!selectedStudent || !currentTerm) return;
    try {
      const updatedMarks = await getMarks(selectedStudent.id, currentTerm.id);
      setMarks(updatedMarks);
    } catch (error) {
      console.error("Error fetching marks:", error);
    }
  };

  // Fetch initial data
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const student = await getStudent(id);
        setSelectedStudent(student);

        const fetchedSubjects = await getSubjects();
        setSubjects(fetchedSubjects);

        const fetchedTerms = await getTerms();
        setTerms(fetchedTerms);

        const now = new Date();
        const activeTerm = fetchedTerms.find(
          (term) => new Date(term.startDate) <= now && now <= new Date(term.endDate)
        );
        setCurrentTerm(activeTerm || fetchedTerms[0]);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchInitialData();
  }, [id]);

  // Fetch marks when student or term changes
  useEffect(() => {
    fetchMarksForStudent();
  }, [selectedStudent, currentTerm]);

  const handleSubjectChange = (e) => {
    const selectedSubject = subjects.find((subject) => subject.name === e.target.value);
    setNewMarkData({ ...newMarkData, subject: selectedSubject });
  };

  const handleMarksSubmit = async (e) => {
    e.preventDefault();
    if (!selectedStudent || !newMarkData.subject || !newMarkData.subject.id) return;

    try {
      await addMarks({
        studentId: selectedStudent.id,
        subjectId: newMarkData.subject.id,
        testType: newMarkData.testType,
        marks: newMarkData.markPercentage,
      });
      await fetchMarksForStudent();
      setIsMarksModalOpen(false);
    } catch (error) {
      console.error("Error adding marks:", error);
    }
  };

  const calculateSubjectTotal = (subjectMarks) => {
    const testMarks = subjectMarks?.test || 0;
    const examMarks = subjectMarks?.exam || 0;
    return testMarks + examMarks;
  };

  const calculateTermTotal = () => {
    return subjects.reduce((total, subject) => {
      const subjectMarks = marks[selectedStudent?.id]?.[currentTerm?.id]?.[subject.name] || {};
      return total + calculateSubjectTotal(subjectMarks);
    }, 0);
  };

  return (
    <div className="StudentDetails-wrapper">
      {/* Side Menu */}
      <div className={`side-menu ${menuOpen ? 'open' : ''}`}>
        <button className="menu-toggle" onClick={toggleMenu}>
          <FontAwesomeIcon icon={faBars} />
        </button>
        <ul>
          <li><Link to="/dashboard"><FontAwesomeIcon icon={faDashboard} /> {menuOpen && <span>Dashboard</span>}</Link></li>
          <li><Link to="/notifications"><FontAwesomeIcon icon={faBell} /> {menuOpen && <span>Notifications</span>}</Link></li>
          <li><Link to="/students"><FontAwesomeIcon icon={faGraduationCap} /> {menuOpen && <span>Students</span>}</Link></li>
          <li><Link to="/events"><FontAwesomeIcon icon={faCalendarAlt} /> {menuOpen && <span>Events</span>}</Link></li>
          <li><Link to="/parents"><FontAwesomeIcon icon={faUsers} /> {menuOpen && <span>Parents</span>}</Link></li>
          <li><Link to="/config"><FontAwesomeIcon icon={faCog} /> {menuOpen && <span>Configurations</span>}</Link></li>
          <li onClick={handleLogout}><FontAwesomeIcon icon={faSignOut} /> {menuOpen && <span>Logout</span>}</li>
        </ul>
      </div>

      {/* Main Content */}
      <div className={`StudentDetails-container ${menuOpen ? 'blur' : ''}`}>
        <header>
          <h1>{currentTerm ? `Current Term: ${currentTerm.name}` : 'No Active Term'}</h1>
        </header>
        <button onClick={() => setIsMarksModalOpen(true)}>+ Add Marks</button>

        {isMarksModalOpen && (
          <div className="marks-modal">
            <form onSubmit={handleMarksSubmit}>
              <select value={newMarkData.subject?.name || ""} onChange={handleSubjectChange}>
                <option value="" disabled>Select Subject</option>
                {subjects.map((subject) => (
                  <option key={subject.id} value={subject.name}>
                    {subject.name}
                  </option>
                ))}
              </select>
              <select value={newMarkData.testType} onChange={(e) => setNewMarkData({ ...newMarkData, testType: e.target.value })}>
                <option value="" disabled>Select Test Type</option>
                <option value="test">Test</option>
                <option value="exam">Exam</option>
              </select>
              <input
                type="number"
                value={newMarkData.markPercentage}
                onChange={(e) => setNewMarkData({ ...newMarkData, markPercentage: e.target.value })}
                min="0"
                max="100"
              />
              <button type="submit">Save</button>
            </form>
          </div>
        )}

        <table>
          <thead>
            <tr>
              <th>Subject</th>
              <th>Test</th>
              <th>Exam</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {subjects.map((subject) => {
              const subjectMarks = marks[selectedStudent?.id]?.[currentTerm?.id]?.[subject.name] || {};
              return (
                <tr key={subject.id}>
                  <td>{subject.name}</td>
                  <td>{subjectMarks.test || '-'}</td>
                  <td>{subjectMarks.exam || '-'}</td>
                  <td>{calculateSubjectTotal(subjectMarks)}</td>
                </tr>
              );
            })}
          </tbody>
          <tfoot>
            <tr>
              <td colSpan="3">Overall Total</td>
              <td>{calculateTermTotal()}</td>
            </tr>
          </tfoot>
        </table>
      </div>
    </div>
  );
};

export default StudentDetailsPage;
