import React, { useState, useEffect } from 'react';
import axios from '../services/apiService';

const Parents = () => {
  const [parents, setParents] = useState([]);
  const [parent, setParent] = useState({ name: '', contact: '' });
  const [error, setError] = useState('');

  useEffect(() => {
    fetchParents();
  }, []);

  const fetchParents = async () => {
    try {
      const response = await axios.get('/parents');
      setParents(response.data);
    } catch (error) {
      setError('Failed to fetch parents');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setParent((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!parent.name || !parent.contact) {
      setError('Please fill out all fields');
      return;
    }
    try {
      await axios.post('/parents', parent);
      fetchParents();
      setParent({ name: '', contact: '' });
    } catch (error) {
      setError('Failed to add parent');
    }
  };

  return (
    <div>
      <h2>Parents</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={parent.name} onChange={handleChange} />
        <input type="text" name="contact" placeholder="Contact" value={parent.contact} onChange={handleChange} />
        <button type="submit">Add Parent</button>
      </form>
      {error && <p>{error}</p>}
      <ul>
        {parents.map((parent) => (
          <li key={parent.id}>{parent.name} - {parent.contact}</li>
        ))}
      </ul>
    </div>
  );
};

export default Parents;
