import React, { useState, useEffect } from 'react';
import axios from '../services/apiService';

const Students = () => {
  const [students, setStudents] = useState([]);
  const [student, setStudent] = useState({ name: '', grade: '' });
  const [error, setError] = useState('');

  useEffect(() => {
    fetchStudents();
  }, []);

  const fetchStudents = async () => {
    try {
      const response = await axios.get('/students');
      setStudents(response.data);
    } catch (error) {
      setError('Failed to fetch students');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStudent((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!student.name || !student.grade) {
      setError('Please fill out all fields');
      return;
    }
    try {
      await axios.post('/students', student);
      fetchStudents();
      setStudent({ name: '', grade: '' });
    } catch (error) {
      setError('Failed to add student');
    }
  };

  return (
    <div>
      <h2>Students</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={student.name} onChange={handleChange} />
        <input type="text" name="grade" placeholder="Grade" value={student.grade} onChange={handleChange} />
        <button type="submit">Add Student</button>
      </form>
      {error && <p>{error}</p>}
      <ul>
        {students.map((student) => (
          <li key={student.id}>{student.name} - {student.grade}</li>
        ))}
      </ul>
    </div>
  );
};

export default Students;
