import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './Dashboard.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell, faUsers, faGraduationCap, faCalendarAlt, faUserCheck, faUserGraduate, faBars, faUserGroup, faUserPlus, faUserPen, faUser, fa6, faSchool, faMagic, faDashboard, faSignOut } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

const API_URL = 'http://localhost:5000';
const Dashboard = () => {
  // State for the metric data (fetched via API)
  const [metrics, setMetrics] = useState({
    sentNotifications: 0,
    registeredParents: 0,
    registeredStudents: 0,
    createdEvents: 0,
    activeParents: 0,
    activeStudents: 0,
  });
  const [user, setUser] = useState(null);
  // State for filters
  const [filter, setFilter] = useState('');
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  // Side menu state
  const [menuOpen, setMenuOpen] = useState(false);

  // Profile dropdown state
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  // Handle profile dropdown toggle
  const toggleProfileDropdown = () => {
    setShowProfileDropdown(!showProfileDropdown);
  };

  // Toggle side menu
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // Fetch user information and metrics from API on component mount
  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`${API_URL}/api/users/me`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        const userData = await response.json();
        setUser(userData);
      } catch (error) {
        console.error('Error fetching user info:', error);
      }
    };

    const fetchMetrics = async () => {
      try {
        const response = await fetch('/api/dashboard-metrics');
        const data = await response.json();
        setMetrics(data); // Set metrics based on API response
      } catch (error) {
        console.error('Error fetching metrics:', error);
      }
    };

    fetchUserInfo();
    fetchMetrics();
  }, []);

  return (
    <div className="dashboard-wrapper">
     {/* Side Menu */}
<div className={`side-menu ${menuOpen ? 'open' : ''}`}>
  <button className="menu-toggle" onClick={toggleMenu}>
    <FontAwesomeIcon icon={faBars} />
  </button>
  {/* Menu Icons */}
  <div className="menu-icons">
  <ul>
        <li><Link to="/dashboard"><FontAwesomeIcon icon={faDashboard} className="Dashboard-icon" /> {menuOpen && <span>Dashboard</span>}</Link></li>
        <li><Link to="/notifications"><FontAwesomeIcon icon={faBell} className="Notifications-icon" /> {menuOpen && <span>Notifications</span>}</Link></li>
        <li><Link to="/students"><FontAwesomeIcon icon={faGraduationCap} className="Students-icon" /> {menuOpen && <span>Students</span>}</Link></li>
        <li><Link to="/events"><FontAwesomeIcon icon={faCalendarAlt} className="Events-icon" /> {menuOpen && <span>Events</span>}</Link></li>
        <li><Link to="/parents"><FontAwesomeIcon icon={faUsers} className="Parents-icon" /> {menuOpen && <span>Parents</span>}</Link></li>
        <li><Link to="/profile-dropdown"><FontAwesomeIcon icon={faUserCheck} className="Profile-icon" /> {menuOpen && <span>Profile</span>}</Link></li>
        <li><Link to="/Logout"><FontAwesomeIcon icon={faSignOut} className="Register-icon" /> {menuOpen && <span>Logout</span>}</Link></li>

      </ul>
  </div>
</div>

      {/* Main Content */}
      <div className={`dashboard-container ${menuOpen ? 'blur' : ''}`}>
        {/* Header Section */}
        <header className="dashboard-header">
          <h1 className="dashboard-title">Dashboard</h1>
        </header>
        {/* Profile Icon */}
        <div className="profile-container" onClick={toggleProfileDropdown}>
            <FontAwesomeIcon icon={faUserCheck} className="profile-icon" />
            {/* Profile Dropdown */}
            {showProfileDropdown && (
              <div className="profile-dropdown">
                <p><strong><i>UserName: {user.username}</i></strong></p>
                <p><strong><i>Email: {user.email}</i></strong></p>
                <button className="logout-button">Logout</button>
              </div>
            )}
          </div>
        {/* Filters Section */}
<div className="dashboard-filters">
  <h2 className="filter-title">Filter By</h2>

  {/* Time Range Filter */}
  <div className="filter-group">
    <label className="filter-label">Time Range:</label>
    <select
      value={filter}
      onChange={(e) => setFilter(e.target.value)}
      className="filter-select"
    >
      <option value="">Select Range</option>
      <option value="today">Today</option>
      <option value="lastweek">Last Week</option>
      <option value="last3months">Last 3 Months</option>
      <option value="year">Year</option>
      <option value="all">All Time</option>
    </select>
  </div>

  {/* From Date */}
  <div className="filter-group">
    <label className="filter-label">From:</label>
    <input
      type="date"
      value={dateRange.start}
      onChange={(e) =>
        setDateRange((prev) => ({ ...prev, start: e.target.value }))
      }
      className="filter-date"
    />
  </div>

  {/* To Date */}
  <div className="filter-group">
    <label className="filter-label">To:</label>
    <input
      type="date"
      value={dateRange.end}
      onChange={(e) =>
        setDateRange((prev) => ({ ...prev, end: e.target.value }))
      }
      className="filter-date"
    />
  </div>

  {/* Action Buttons */}
  <div className="filter-buttons">
    <button className="clear-button" onClick={() => { /* Clear Filters Logic */ }}>
      Clear
    </button>
    <button className="apply-button" onClick={() => { /* Apply Filters Logic */ }}>
      Filter
    </button>
  </div>
</div>
        {/* Metrics Section */}
        <div className="metrics-container">
          <div className="metric-card">
            <FontAwesomeIcon icon={faBell} className="metric-icon" style={{ color: 'blue' }} />
            <h2>Sent Parent Notifications</h2>
            <p>{metrics.sentNotifications}</p>
          </div>
          <div className="metric-card">
            <FontAwesomeIcon icon={faUsers} className="metric-icon" style={{ color: 'green' }} />
            <h2>Registered Parents</h2>
            <p>{metrics.registeredParents}</p>
          </div>
          <div className="metric-card">
            <FontAwesomeIcon icon={faGraduationCap} className="metric-icon" style={{ color: 'purple' }} />
            <h2>Registered Students</h2>
            <p>{metrics.registeredStudents}</p>
          </div>
          <div className="metric-card">
            <FontAwesomeIcon icon={faCalendarAlt} className="metric-icon" style={{ color: 'orange' }} />
            <h2>Created Events</h2>
            <p>{metrics.createdEvents}</p>
          </div>
          <div className="metric-card">
            <FontAwesomeIcon icon={faUserCheck} className="metric-icon" style={{ color: 'red' }} />
            <h2>Active Parents</h2>
            <p>{metrics.activeParents}</p>
          </div>
          <div className="metric-card">
            <FontAwesomeIcon icon={faUserGraduate} className="metric-icon" style={{ color: 'brown' }} />
            <h2>Active Students</h2>
            <p>{metrics.activeStudents}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
